--
-- 转存表中的数据 `dasai`
--

INSERT INTO `dasai` (`id`, `title`, `pic`, `tuwen`, `startday`, `endday`, `readcnt`) VALUES
(3, '最美秋季摄影大赛', '1504937727.jpg', '<div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.1球台的上层表面叫做比赛台面，应为与水平面平行的长方形，长2.74米，宽1.525米，由地面向上至台面高76厘米。</div><div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.2比赛台面不包括球台台面的侧面。</div><div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.3比赛台面可用任何材料制成，应具有一致的弹性，即当标准球从离台面30厘米高处落至台面时，弹起高度应约为23厘米。<div class="lemma-picture text-pic layout-right" style="box-sizing: border-box; margin: 0px 0px 3px; padding: 0px; outline: none; border: 1px solid rgb(224, 224, 224); overflow: hidden; position: relative; float: right; clear: right; width: 220px;"><a class="image-link" href="http://undefined" target="_blank" style="box-sizing: border-box; margin: 0px; padding: 0px; outline: none; background-color: transparent; color: rgb(19, 110, 194); display: block; width: 220px; height: 165px;"><img class="" src="https://gss3.bdstatic.com/7Po3dSag_xI4khGkpoWK1HF6hhy/baike/s%3D220/sign=7e1a4ef46209c93d03f209f5af3cf8bb/c995d143ad4bd113c4fdb3c85fafa40f4bfb0513.jpg" alt="" style="box-sizing: border-box; margin: 0px auto; padding: 0px; outline: none; display: block; width: 220px; height: 165px;"/></a></div></div><div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.4比赛台面应呈均匀的暗色，无光泽，沿每个2.74米的比赛台面边缘各有一条2厘米宽的白色边线，沿每个1.525米的比赛台面边缘各有一条2厘米宽的白色端线。</div><div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.5比赛台面由一个与端线平行的垂直的球网划分为两个相等的台区，各台区的整个面积应是一个整体。</div><div class="para" style="box-sizing: border-box; margin: 0px 0px 15px; padding: 0px; outline: none; font-size: 14px; white-space: normal; word-wrap: break-word; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;">1.1.6双打时,各台区应由一条3毫米宽的白色中线,划分为两个相等的“半区”。中线与边线平行，并应视为右半区的一部分。</div><p><br/></p>', '2017-09-06 10:50:35', '2017-09-08 13:45:36', 57);

