--
-- 表的结构 `toupiao`
--

CREATE TABLE IF NOT EXISTS `toupiao` (
  `id` int(11) NOT NULL,
  `uid` int(11) NOT NULL COMMENT '会员id',
  `wid` int(11) NOT NULL COMMENT '作品id',
  `ttime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '投票时间',
  `tday` date NOT NULL
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

