INSERT INTO `userattr` (`id`, `uid`, `head`, `uname`, `sex`, `age`, `addr`, `mryst`, `height`, `intro`) VALUES
(2, 6, '6_1504765814.jpg', '浊酒杯醉人不醉', '男', 25, '辽宁-丹东', '未婚', 0, 'asdcjas8u chasui chasuidc'),
(3, 7, '7_1504535460.jpg', 'lisa', '女', 28, '成都', '已婚', 0, '我是一个开朗又多才多艺的女孩，画画，写字，弹琴，喜欢旅游，看书都是我的爱好，'),
(4, 8, '8_1504535737.jpg', '艾米', '女', 25, '宁波', '未婚', 0, '本人性格活泼开朗、工作认真仔细、具有很好的亲和力，善于与人沟通、而且爱好广泛，爱好唱歌、写作、喜欢不断创新、追求新事物'),
(5, 13, '13_1504536467.jpg', '吉姆', '男', 29, '长沙', '离异', 0, '我来自长沙，目前离异，希望能通过这个平台找到我的另一半。。。。。。'),
(6, 15, '15_1504536688.jpg', '天意', '男', 23, '杭州', '未婚', 0, '我是小鲜肉一枚，，请来找我吧。。。。'),
(7, 16, '16_1504536964.jpg', '罗米克', '男', 26, '苏州', '未婚', 0, '本人性格开朗、稳重、有活力,待人热情、真诚;工作认真负责,积极主动,能 吃苦耐劳,用于承受压力,勇于创新'),
(8, 17, '17_1504537218.jpg', '烟花一笑', '女', 23, '成都', '未婚', 0, '本人性格开朗、稳重、有活力，待人热情、真诚；工作认真负责，积极主动，能吃苦耐劳，用于承受压力，勇于创新；'),
(9, 19, '19_1504708357.jpg', '素颜如水ノ', '女', 24, '大连', '未婚', 0, '夜晚来了我还依然睁着眼睛，是因为我看见了你留在月光下的痕迹。'),
(10, 21, '21_1504708455.jpg', '相逢何必曾相识', '女', 26, '长沙', '未婚', 0, '你在桥上看风景，我在楼上看着你。我从未离开你，你一直在我心里'),
(11, 22, '22_1504708563.jpg', '胖次超人', '男', 28, '成都', '离异', 0, '我不是对谁都三言两语，我不是对谁都好脾气，我不是对谁都念旧情，当我惯着你的时候请珍惜！'),
(12, 23, '23_1504708681.jpg', '新一代丶型男', '男', 26, '福州', '未婚', 0, '最初的倔强固执骄傲早已被时间磨破， 当你成熟的时候其实也被毁的差不多了'),
(13, 24, '24_1504708818.jpg', '书尽', '男', 25, '成都', '未婚', 0, '将一切都看破，自己放心里难受，这才叫成熟'),
(14, 25, '25_1504708890.jpg', '林殊', '男', 27, '成都', '离异', 0, '明日复明日，明日何其多！既然这么多，不妨再拖拖。'),
(15, 26, '26_1504708972.jpg', '贾彰', '男', 25, '拉萨', '未婚', 0, '有一只羊在唱歌：把你的肉我的肉串一串，串一个羊肉串，再串一个羊肉串'),
(16, 27, '27_1504709047.jpg', '安静的聆听那一抹辉煌╮', '女', 25, '成都', '未婚', 0, '为君沉醉又何妨？只怕酒醒时候、断人肠！'),
(17, 28, '28_1504709121.jpg', '良人情未深', '女', 23, '成都', '未婚', 0, '人生若只如初见，何事秋风悲画扇'),
(18, 29, '29_1504709198.jpg', '林溪儿', '女', 25, '宁波', '未婚', 0, '成熟的人会珍惜自己的爱人如同珍惜自己的眼睛，因为唯有珍惜爱情，爱情才不会老来找你的麻烦。'),
(19, 30, '30_1504709287.jpg', '莫二燕', '女', 28, '西昌', '离异', 0, '你是一场盛大的梦 梦醒了我能怪谁 You are a grand dream wake up I can blame'),
(20, 31, '31_1504709385.jpg', '林白依', '女', 25, '昆明', '未婚', 0, '雨歇凭栏处、渐去月华影、不见归时不闻音。'),
(21, 32, '32_1504709691.jpg', 'sacacs', '男', 23, '哈哈', '未婚', 0, '草的三从四德'),
(22, 33, '33_1504792355.jpg', '萌萌', '女', 25, '成都', '未婚', 0, '一念心清静，莲花处处开。。。。。。。。。'),
(23, 34, '34_1504792435.jpg', '法克鱿小面包', '女', 25, '沈阳', '未婚', 0, '我关心的你都无所谓，你在乎的我都不了解，这大概就是为什么我走不进你的世界。'),
(24, 35, '35_1504792490.jpg', '女人不花丶何来貌美如花', '女', 23, '长沙', '未婚', 0, '一切处无心是净，得净之时不得作净想，名无净 得无净时，亦不得作无净想，是无无净。'),
(25, 36, '36_1504792576.jpg', '帆布鞋也比你高跟鞋优雅、', '女', 26, '丽江', '未婚', 0, '人生在世如身处荆棘林中，心不动则人不妄动，不动则不伤；如心动则人妄动，则伤其身痛其骨，于是体会到世间诸般痛苦。'),
(26, 38, '38_1504792739.jpg', '逗比男神i', '男', 23, '常州', '未婚', 0, '在角落唱沙哑的歌，再大声也都是给你，请用心听，不要说话。'),
(27, 39, '39_1504792820.jpg', '宁泽民', '男', 27, '北京', '离异', 0, '真正的所谓的修养，真正的所谓的用功，真正的所谓的感受，其实心性，心田里面进行的。'),
(28, 40, '40_1504792895.jpg', '吴心来', '男', 25, '泽州', '未婚', 0, '修行是要在日常生活里保持觉悟的心，活在此时此刻，活得心安理得'),
(29, 41, '41_1504792985.jpg', '我是精神病人@', '男', 24, '桂林', '未婚', 0, '一切在旁人眼里的好运，只有自己心知肚明这是必然。成年人应该有这样的觉悟'),
(30, 42, '42_1504793189.jpg', '临西', '男', 24, '北京', '未婚', 0, '菩提本无树，明镜亦非台。本来无一物，何处惹尘埃'),
(31, 43, '43_1504793277.jpg', '九公里浅绿', '男', 23, '大理', '未婚', 0, '时间流逝不会让人成熟，要走过很多的路，经历过生命中无数突如其来的繁华和苍凉后，才会变得成熟。'),
(32, 44, '44_1504793359.jpg', '刘攀沉', '男', 25, '六盘水', '未婚', 0, '人总有那么一段时间要学会自己长大，笑着转身那才是最好的你。'),
(33, 45, '45_1504793467.jpg', '陈锡祉', '男', 24, '贵州', '未婚', 0, '经历越多就越不想说话，环境的不同，想说的话别人未必能懂，也就慢慢学会了自己默默承受。'),
(34, 46, '46_1504793536.jpg', '宁茜茜', '女', 25, '厦门', '未婚', 0, '所谓长大成人，就是不断聚了散，散了又聚，为了让彼此不会受伤害而保持一个适当的距离。'),
(35, 47, '47_1504793607.jpg', '张文敏', '女', 26, '长沙', '离异', 0, '天生敏感的人光是调整情绪就花费太多时间了，所以我们成长得慢一些，也是可以理解的吧。'),
(36, 48, '48_1504793711.jpg', '温尔缦', '女', 24, '呼和浩特', '已婚', 0, '长大后才发现，有时你说了真话，你还得和人家道歉，因为你戳穿了事实，所以想要活得顺畅，请时刻带上脑子。'),
(37, 49, '49_1504793807.jpg', 'っ好好的做个小布丁', '女', 25, '内蒙古', '已婚', 0, '凡是一切适悦人心的感受都称为乐受，一切使人心不适悦的感受都称为苦受，它包括外境、身体、心理所引起，'),
(38, 51, '51_1504794013.jpg', '林梦夕', '女', 26, '大理', '未婚', 0, '成熟的人会珍惜自己的爱人如同珍惜自己的眼睛，因为唯有珍惜爱情，爱情才不会老来找你的麻烦。'),
(39, 52, '52_1504794482.jpg', '美美', '女', 26, '长沙', '已婚', 0, '心不动则人不妄动，不动则不伤；如心动则人妄动，则伤其身痛其骨，于是体会到世间诸般痛苦。');

