  `id` int(11) NOT NULL,
  `uid` int(11) NOT NULL COMMENT '用户id',
  `yid` int(11) NOT NULL COMMENT '游记id',
  `content` varchar(300) NOT NULL,
  `ltime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

