  `id` int(11) NOT NULL,
  `title` varchar(50) NOT NULL,
  `pic` varchar(20) NOT NULL,
  `tuwen` text NOT NULL,
  `readcnt` int(11) NOT NULL DEFAULT '0',
  `ptime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=MyISAM AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;

