CREATE TABLE `dongtai` (
  `id` int(11) NOT NULL,
  `uid` int(11) NOT NULL,
  `content` varchar(300) NOT NULL COMMENT '内容',
  `imgs` varchar(200) NOT NULL COMMENT '图片',
  `ftime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `readcnt` int(11) NOT NULL DEFAULT '0' COMMENT '阅读次数',
  `zancnt` int(11) NOT NULL DEFAULT '0' COMMENT '点赞次数',
