--
-- 表的结构 `toupiao`
--

CREATE TABLE `toupiao` (
  `id` int(11) NOT NULL,
  `uid` int(11) NOT NULL COMMENT '会员id',
  `wid` int(11) NOT NULL COMMENT '作品id',
  `ttime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '投票时间',
  `flag` int(11) NOT NULL DEFAULT '1' COMMENT '备用'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- --------------------------------------------------------

--
-- 表的结构 `tubuhuodong`
--

CREATE TABLE `tubuhuodong` (
  `id` int(11) NOT NULL,
  `title` varchar(60) NOT NULL COMMENT '活动标题',
  `tuwen` text NOT NULL COMMENT '图文介绍',
  `types` varchar(10) NOT NULL COMMENT '活动类型',
  `howlong` int(11) NOT NULL COMMENT '行程多少天',
  `startday` varchar(20) NOT NULL COMMENT '开始日期',
  `endday` varchar(20) NOT NULL COMMENT '结束日期',
  `price` int(11) NOT NULL COMMENT '价格',
  `mudidi` varchar(30) NOT NULL COMMENT '目的地',
  `jingdian` varchar(100) NOT NULL COMMENT '景点',
  `neirong` varchar(30) NOT NULL COMMENT '活动内容',
  `jihetime` varchar(30) NOT NULL COMMENT '集合时间',
  `jihedidian` varchar(20) NOT NULL COMMENT '集合地点',
  `qiangdu` varchar(10) NOT NULL COMMENT '徒步强度',
  `juli` varchar(10) NOT NULL COMMENT '徒步距离',
  `jiaotong` varchar(10) NOT NULL COMMENT '交通方式',
  `need` int(11) NOT NULL COMMENT '需要多少人',
  `baoming` int(11) NOT NULL DEFAULT '0' COMMENT '当前报名人数',
  `phone` char(12) NOT NULL COMMENT '领队电话',
  `leader` varchar(10) NOT NULL COMMENT '领队',
  `pictures` varchar(150) NOT NULL COMMENT '活动封面',
  `tese` varchar(100) NOT NULL COMMENT '线路特色',
  `ptime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '活动发布时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='徒步活动表';

--
-- 转存表中的数据 `tubuhuodong`
--

INSERT INTO `tubuhuodong` (`id`, `title`, `tuwen`, `types`, `howlong`, `startday`, `endday`, `price`, `mudidi`, `jingdian`, `neirong`, `jihetime`, `jihedidian`, `qiangdu`, `juli`, `jiaotong`, `need`, `baoming`, `phone`, `leader`, `pictures`, `tese`, `ptime`) VALUES
(7, '【圣地】CVA带你走进心向天堂-西班牙圣地亚哥朝圣之路(徒步5天111km)【精品】', '<div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">行程安排</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"mdd-zj\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><span style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium; line-height: 25.6px;\">路线：</span><span style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: medium; line-height: 25.6px;\">北京——马德里——莱昂——阿斯托加Astorga——蓬费拉达Ponferrada——俄塞布里尔O Cebreiro——撒利亚Sarria——波特马林Portomarin——帕拉斯德雷Palas de Rei——阿尔苏阿Arzua——佩德罗索 Pedrouzo——圣地亚哥——波尔图——里斯本——辛特拉——罗卡角——里斯本——北京。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817141153_67681.jpg\" width=\"600\" height=\"392\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D1 / 北京 -- &nbsp;马德里 &nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;（航班待定）到达后， 我们前往入住酒店休息。 机上餐。 &nbsp; &nbsp;/&nbsp;&nbsp; 住宿：马德里</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D2 / 马德里 &nbsp;&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">‘阳光优雅地漫步旅店的草坪，人鱼在石刻墙壁弹奏着竖琴’—《马德里不思议》。古老而繁华的格兰维亚大街、充满艺术气息的马约尔广场、大教堂，赖着阳光端着红色桑格利亚汽酒的人们，马德里的每一处角落都透着霸气的旧日辉煌和优雅的现代慷懒，就让我们从马德里不思议开始我们的旅行吧；其他亮点：太阳门、西班牙广场、大地女神喷泉、邮政大楼、阿尔卡拉门、西班牙最大的斗牛场、阿多查火车站。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：城市休闲游，约12km。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 16px;\">马德里·大地女神喷泉</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816142629_30043.jpg\" width=\"600\" height=\"399\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 20px; line-height: 30px;\">马德里·太阳门广场</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817150011_58766.jpg\" width=\"600\" height=\"440\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112108_95013.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112210_61794.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112109_68059.jpg\" width=\"600\" height=\"338\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D3 / 马德里 - 莱昂 335KM &nbsp;行车&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">莱昂，一个已经在世界上消逝的王国，最初成立是作为罗马军第七军团的营地，后来发展成为中世纪一个王国的首都。莱昂辉煌的历史给人类留下了丰富的文化遗产，他也是朝圣之路必经之路。莱昂的重要建筑有：莱昂大教堂、圣依希德罗教堂和圣马哥修道院。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">拥有700多年历史的莱昂大教堂*，其高耸入云的塔尖和拱璧，以及惊人的内部设计代表着城市的精神中心。大教堂作为西班牙首屈一指的哥特式杰作，其内部却弥漫着柔美的光彩，明亮的玫瑰花彩窗和镶满彩色玻璃窗的走廊，是教堂最美丽最壮观的特色。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">圣依希德罗教堂最早可追溯到1063年。其中的万神殿安放着20多位君主的遗体，然而19世纪初不幸被拿破仑军队洗劫一空。教堂的主体融合了多种风格，但正门的大门采用了纯粹的罗马式风格，教堂内部的彩绘是值得观赏的亮点。圣马可修道院是西班牙文艺复兴时期的典型代表，最初是12世纪为去圣地亚哥的朝圣者提供住宿而建的修道院，之后改建为圣地亚哥骑士军总部，现已改建为国营古堡酒店。其它知名景点：西班牙天才建筑师高迪设计的新哥特式风格的伯提内之家，Palacio de Los Guzmanes宫殿，马约尔广场。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：城市休闲游，约11km。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 16px; line-height: 30px;\">莱昂·莱昂大教堂</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817160752_15815.jpg\" width=\"600\" height=\"453\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111018_58783.jpg\" width=\"600\" height=\"800\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D4 / 莱昂 – 阿斯托加Astorga 51KM – 蓬费拉达Ponferrada 62KM –俄塞布里尔O Cebreiro 53KM – 撒利亚Sarria 45KM &nbsp;行车</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">阿斯托加，这座古城曾是银路（Via de la plata）上战略地位很重要的要塞，银路是一条古罗马公路，贯穿安达卢西亚和西班牙西北部，后来阿斯托加又成为圣地亚哥朝圣路上的一座重要驿站。城中高高耸立的是两座纪念性建筑---大教堂和主教宫（外观）。值得一提的是这座神话般的由形态各异的小塔楼组成的主教宫，是由天才建筑设计师高迪设计。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">蓬费拉达，这个城市的名字来源于一座为朝圣者建立的桥的名字，蓬费拉达雄伟的城堡是圣殿骑士于12-14世纪间建立，目的是保护圣地朝圣者。在中世纪，这里曾是西班牙西北部最大的堡垒之一。我们将在城堡脚下的餐厅品尝当地特色：猪蹄煲。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">俄塞布里尔依山而建，已从属加里西亚省，与里昂接壤。俄镇是朝圣之路上最不同寻常的一个村镇，圣经载：1300年，建于9世纪的教堂见证了一场奇迹，这里的葡萄酒流成了鲜血的河流，面包变成了肉。这里还保留了很多朝圣者寄宿过的盖着茅草顶的石屋，是典型的古凯尔特风格。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">撒利亚是我们朝圣之路徒步旅行的起点，到达撒利亚后，我们到Iglesia santa marina教堂取得护照。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><a style=\"color: rgb(51, 51, 51); outline: none; transition: all 0.2s ease-in-out;\" href=\"http://undefined\"><span style=\"font-size: 16px;\">阿斯托加·阿斯托加古堡</span></a><span style=\"font-size: 16px;\"></span><span style=\"font-size: 16px;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816144052_46358.jpg\" width=\"600\" height=\"483\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\">这已经是私人滴啦~</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 20px; line-height: 32px;\">蓬费拉达Ponferrada教堂城堡</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817155047_11894.jpeg\" width=\"600\" height=\"318\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111736_38929.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111736_48908.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112257_81979.jpg\" width=\"600\" height=\"800\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D5 / 撒利亚 – 波特马林Portomarin 徒步</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：22.5公里，海拔上升395km。 &nbsp; 穿越小村镇：Barbadelo,Ferreiros</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">难度系数：中； &nbsp; &nbsp;途中风光：栎树林、乡间小道、广阔山野、河流、村庄； &nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112715_49822.jpg\" width=\"600\" height=\"338\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112108_95875.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816144248_70204.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"line-height: 1.5;\">开拔！</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D6 / 波特马林 – 帕拉斯德雷Palas de Rei &nbsp;徒步</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：25公里； &nbsp; &nbsp;穿越小村镇：Gonzar, O Hospital da Cruz, Ligonde</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">难度系数：中； &nbsp; &nbsp;途中风光：山峦、小牧场、村子、公路； &nbsp; &nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><span style=\"line-height: 1.5;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816150215_50569.jpg\" width=\"600\" height=\"393\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111848_74416.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111848_13240.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><span style=\"line-height: 1.5;\">D7 / 帕拉斯德雷 – 阿尔苏阿Arzua &nbsp;徒步&nbsp;</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：29公里； &nbsp; &nbsp; 穿越小村镇：Mato-Casanova, Melide, Ribadiso； &nbsp; &nbsp; &nbsp;夜宿阿尔苏阿</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">难度系数：高； &nbsp; &nbsp;途中风光：森林、中世纪古村落Ribadiso、小河、古桥Puente La Reina</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816150249_97542.jpg\" width=\"600\" height=\"384\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111935_15937.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111935_80590.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112454_75359.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D8 / 阿尔苏阿 – 佩德罗索 Pedrouzo徒步</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：21公里 &nbsp; &nbsp;穿越小村镇：Santa Irene； &nbsp; 夜宿佩德罗索 &nbsp; 难度系数：中； 途中风光：原始森林、乡间小路、小村庄</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817112946_96069.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D9 / 佩德罗索 Pedrouzo-Arca – 圣地亚哥 徒步</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">徒步距离：19公里 &nbsp; &nbsp; &nbsp; 穿越小村镇：Monte do Gozo &nbsp;难度系数：中； &nbsp; &nbsp; &nbsp; 途中风光：山林、小村庄、古城圣地亚哥</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">到达圣城---圣地亚哥.德.孔波斯特拉，和其他朝圣者一样，来到圣地亚哥大教堂前席地而坐，抬头瞻仰这座神圣的大教堂。之后走进教堂，把自己当成一个真正的朝圣者，向伟大的圣人圣地亚哥致以最虔诚的膜拜。带上朝圣护照，到朝圣证书办公室领取朝圣者证书。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">入住圣地亚哥酒店。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 18px;\">圣地亚哥大教堂</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817145719_28622.jpg\" width=\"600\" height=\"821\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">D10 / 圣地亚哥 –波尔图230ＫＭ</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">早上前往圣地亚哥，参加圣地亚哥大教堂摆荡香炉仪式。之后前往波尔图，到达后入住酒店休息。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 18px;\">圣地亚哥大教堂摆荡香炉仪式</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\">这次的RP如何呢？图为世界最大的香炉仪式！</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816145150_69849.jpg\" width=\"600\" height=\"375\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816145150_94592.jpg\" width=\"600\" height=\"374\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"font-size: 18px; line-height: 30px;\">波尔图</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816144607_36897.jpg\" width=\"600\" height=\"400\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816144607_21217.jpg\" width=\"600\" height=\"398\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170816/20170816144607_97750.jpg\" width=\"600\" height=\"421\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111154_93558.jpg\" width=\"600\" height=\"800\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170817/20170817111154_97002.jpg\" width=\"600\" height=\"450\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none; width: 672px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">Ｄ11/ 波尔图—里斯本 &nbsp;320km</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">坐落在多罗河河口的波尔图，是葡萄牙的第二大城市，多罗河蜿蜒穿越整个城市，城中历史著名建筑众多。更为重要的是，波尔图是多罗河上游葡萄酒的集散地，品质甘醇的波尔图葡萄酒被誉为葡萄牙的&quot;第一大使&quot;。波尔图的杜罗河口壮观的地理景色以及一直保存完好的建筑遗迹，在1996年被联合国教科文组织列为世界遗产</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">之后前往里斯本，它是葡萄牙的首都和最大的港口，坐落在特茹河的入海口。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\">按时间徒步经典场所。</p></div></div><p><br/></p>', '4', 6, '2017年10月11日', '2017年10月18日', 26800, '国外,圣地亚哥', '圣地亚哥朝圣之路：圣地亚哥朝圣之路始于9世纪，被称为欧洲最早的文化旅游指南。传说耶稣的门徒圣地亚哥在当时十分偏僻的西班牙', '休闲,徒步,摄影,国外', '2017年10月11日', '北京首都机场', '1', '450km', '包车', 7, 0, '18328402805', '骆驼祥子', '1504421001.jpg#1504421004.jpg#1504421006.jpg#1504421008.jpg#1504421011.jpg#1504421014.jpg', '曾几何时对差速度和擦拭的和擦哈哈是一场撒ｕｉｃ', '2017-09-04 03:27:25'),
(8, '问道武当——亘古无双胜境，天下第一仙山', '<div class=\"mdd-item\" id=\"d-hd-1\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">行程安排</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"mdd-zj\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">该行程为参考路线，具体以成行后为准。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">D1（9.30号）：确定行程后，包车或火车出发去武当山景区。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">D2（10.1号）：各地前往武当山景区集合（武当山站），8点左右抵达徒步起点，中午抵达五龙宫，徒步峡谷，晚上宿南岩 。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">D3（10.2号）：5点起床看日出，用完早餐后游览太子坡，逍遥谷， 紫霄宫， 南岩宫 。晚上宿南岩。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">D4（10.3号）：早上看完日出游览金顶，途径榔梅祠，黄龙洞，朝天宫，一天门，二天门，三天门，11点左右开始下山，游琼台 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;15点下山。18点出发返京。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\">D5（10.4号）：到达北京。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: large; font-family: 宋体; text-indent: 2em;\"><br/></p></div></div></div><div class=\"mdd-item\" id=\"d-hd-2\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">费用说明</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"con\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 16px;\">1、住宿 ：2晚双人标间住宿，独立卫浴。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 16px;\">3、领队费及相关其他费用。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 16px;\">费用不包含：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 18px;\"><span style=\"font-family: 宋体;\">1、以上不包含餐费，T票失败产生的门票、金顶门票27。</span></p><h4 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 18px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 16px;\">3、不可抗拒因素及其他个人产生的一切费用。</span></span></h4><p><span style=\"font-family: 宋体;\"><span style=\"font-size: 16px;\"><br/></span></span></p><h4 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\" class=\"con\" id=\"d-hd-3\"><span style=\"font-size: 16px;\">武当山，道教圣地，位于湖北省西北部的十堰市丹江口市境内。武当山又名太和山、谢罗山、参</span></h4><h4 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\" class=\"con\" id=\"d-hd-3\"><span style=\"font-size: 16px;\"><br/></span></h4><h4 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\" class=\"con\" id=\"d-hd-3\"><span style=\"font-size: 16px;\">上山、仙室山，古有“太岳”、“玄岳”、“大岳”之称。</span></h4><p></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><span style=\"font-size: 16px;\">明代，武当山被皇帝封为“大岳”、“治世玄岳”，被尊为至高无上的“皇室家庙”。武当山以“四大名山皆拱揖，五方仙岳共朝宗”的“五岳之冠”的显赫地位闻名于世。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><span style=\"font-size: 16px;\">武当武术，是中华武术的重要流派。元末明初，道士张三丰集其大成，开创武当派，并影响至今。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><span style=\"font-size: 16px;\">大家可以提前观看《问道武当》，以便更多了解武当山《问道武当》纪录片地址<a href=\"http://undefined\" target=\"_blank\" style=\"color: rgb(51, 51, 51); outline: none; transition: all 0.2s ease-in-out;\">http://www.bilibili.com/video/av5479535/</a></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170801/20170801102659_76706.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170801/20170801102358_89122.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170801/20170801102526_17429.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170801/20170801102551_82533.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170801/20170801102615_90647.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);\"><br/></p>&lt;p font-size:14px;background-color:#ffffff;&quot;=&quot;&quot; style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102);&quot;&gt;<span style=\"font-size: 16px;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170731/20170731185531_51830.png\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170731/20170731185532_69739.png\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170731/20170731185534_63475.png\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170731/20170731185535_13155.png\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></span></div></div></div><p><br/></p>', '7', 3, '2017年09月30日', '2017年10月05日', 680, '湖北,十堰', '武当山', '休闲,登山,穿越,徒步,摄影,背包旅行', '2017年09月30日', '暂定惠新', '3', '', '包车', 35, 0, '18328402805', '科比行者大树', '1504422067.jpg#1504422068.jpg#1504422071.jpg#1504422073.jpg#1504422075.jpg#1504422077.jpg', '', '2017-09-03 07:01:20');
INSERT INTO `tubuhuodong` (`id`, `title`, `tuwen`, `types`, `howlong`, `startday`, `endday`, `price`, `mudidi`, `jingdian`, `neirong`, `jihetime`, `jihedidian`, `qiangdu`, `juli`, `jiaotong`, `need`, `baoming`, `phone`, `leader`, `pictures`, `tese`, `ptime`) VALUES
(9, '2017年11月3-5日泰山+岱崮两晚三天', '<p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 20px;line-height: 32px\">&nbsp;<span style=\"font-size: 24px\">&nbsp;D1/11</span></span></strong><strong><span style=\"font-size: 24px;line-height: 32px\">月3</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\"><span style=\"font-size: 24px\">日</span>晚21点前到泰山脚下，会联系好泰山脚下红门附近旅馆，火车以</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">T31</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">，和</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">G201</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">为好，</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">22</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">点前入住，休息。</span></strong><strong></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 20px;line-height: 32px\">&nbsp;&nbsp;<span style=\"font-size: 24px\">&nbsp;D2/11</span></span></strong><strong><span style=\"font-size: 24px;line-height: 32px\">月4</span></strong><strong><span style=\"font-size: 24px;line-height: 32px\">日</span></strong><strong></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">3:00 &nbsp;</span><span style=\"font-size: 24px\">起床</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">5</span><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">：30 登顶看日出</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">7:00 &nbsp;</span><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">山顶游玩照相1小时</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">8:30&nbsp;</span><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">轻松下山，不走回头路，走后山徒步</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">14:00 &nbsp;</span><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">下到山底</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">16:00 &nbsp;前</span><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">集体吃饭</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">16:30 &nbsp;包车前往岱崮</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">20：30 &nbsp;到达岱崮，入住景区。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 18px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;line-height: 32px\">D3/11</span></strong><strong><span style=\"font-size: 24px;line-height: 32px\">月5</span></strong><strong><span style=\"font-size: 24px;line-height: 32px\">日</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp;&nbsp;</span><span style=\"font-size: 24px\">&nbsp;5;00 起床早饭</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp; 6:00 爬山，至少走两个崮。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp; 12:00 午饭自理。有时间再多走一个崮。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp; 14:00 送火车站。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp; 买K1902平邑--北京17:08---次日5:00到北京站，或K52，平邑--北京站19:40---次日5:20.</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\"><strong><span style=\"line-height: 32px\">D4/11</span></strong><strong><span style=\"line-height: 32px\">月6</span></strong><strong><span style=\"line-height: 32px\">日</span></strong><br/></span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\"><span style=\"line-height: 24px\">&nbsp; &nbsp;早5:30到达北京（正常上班）</span></span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 20px;line-height: 32px\"><br/></span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 20px;line-height: 32px\">备注：网上报名明后请通知领队，</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">13381033689</span></strong><strong><span style=\"font-size: 20px;line-height: 32px\">张姐，马上加微信建群，火车票自己按上面的通知买，几个人报名都成型，每年都去，路线不错，领队必去，放心！</span></strong></p><h3 style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;height: 45px;line-height: 45px;color: rgb(75, 142, 232);font-weight: normal;font-size: 18px\">费用说明</h3><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px\">活动费用：会员价78</span></strong><strong><span style=\"font-size: 24px\">0</span></strong><strong><span style=\"font-size: 24px\">元</span></strong><strong><span style=\"font-size: 24px\">&nbsp;</span></strong><strong><span style=\"font-size: 24px\">。</span></strong><strong></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">含活动保险、每天2瓶水、领队费用等，两晚住宿，泰安当地一晚山下住宿，岱崮住一宿（至少2正餐+2早餐），门票（泰山），岱崮门票，去岱崮包车（不含接送站）。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">费用不含：各地到泰山的往返火车和接送站。两天爬山中午餐自备。体弱自备功能饮料。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">门票有各种证件的，按实际发生费用退费。</span></strong></p><p><strong><br/></strong></p><h3 style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;height: 45px;line-height: 45px;color: rgb(75, 142, 232);font-weight: normal;font-size: 18px\">线路描述</h3><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 4px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;line-height: 24px;font-family: 微软雅黑, sans-serif\">一、活动时间：2017年11月3--5日</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;font-family: 微软雅黑, sans-serif\">二、活动主题：</span></strong><span style=\"font-size: 24px\">登泰山穿越，看日出，去岱崮一个非常值得一去的地方</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;line-height: 24px;font-family: 微软雅黑, sans-serif\">三、活动内容：</span></strong><span style=\"font-size: 24px;line-height: 24px;font-family: 微软雅黑, sans-serif\">登AAAAA山、拉体能线路，挑战自己</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 4px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;line-height: 24px;font-family: 微软雅黑, sans-serif\">四、针对人群：有一段时间登山经历，适合经常锻炼的人，新人也可以尝试。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 4px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 24px;line-height: 24px;font-family: 微软雅黑, sans-serif\">五、活动难度：第一天台级路为主，全程20公里，上升1200米</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 4px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-family: 微软雅黑, sans-serif;font-size: 24px\">六、活动路线/CVA级别：CVA:1.5</span></strong><span style=\"font-family: 微软雅黑, sans-serif;font-size: 24px\">级 &nbsp;</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">参加活动的朋友请加</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: rgb(146, 208, 80)\"><span style=\"color: rgb(0, 176, 80)\"><span style=\"font-size: 24px\">QQ群 &nbsp; &nbsp; &nbsp; &nbsp; 徒步网主群：94419616</span></span></span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">&nbsp; &nbsp; &nbsp;微博 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;@中国徒步网</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"><span style=\"color: rgb(0, 176, 240);font-size: 24px\">&nbsp; &nbsp; &nbsp;微信 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;微信号：chinawalking</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"><span style=\"color: rgb(0, 176, 240);font-size: 24px\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;二维码：</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;text-align: center;background-color: rgb(243, 243, 243)\"><img src=\"http://new.chinawalking.net.cn/php/upload/65561474366931.jpg\" title=\"公众号.jpg\" width=\"200\" height=\"194\" style=\";padding: 0px;border-width: initial;border-style: none;width: 200px;height: 194px\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">&nbsp;http://www.chinawalking.net.cn/forum_travelview.php?newsid=61</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: tahoma, verdana;background-color: rgb(243, 243, 243)\"><span style=\"font-size: 24px\">张姐户外简历</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 40px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;font-family: 微软雅黑, sans-serif\">【泰山介绍】国家5A景区，</span></strong><strong><span style=\"font-size: 16px;font-family: 微软雅黑, sans-serif\">泰山的<em><strong>海拔</strong></em>是1545米，为中华五岳之首，亦称岱山、岱宗、岱岳、东岳、泰岳等，名称之多，实为全国名山之冠。它同衡山、恒山、华山、嵩山合称五岳，因地处东部，故称东岳。几千年来，泰山一直是历代帝王封禅祭天的神山，因而享有“五岳之长”的称号。佛道两家、文人名人也纷至沓来，在此留下众多文化遗迹。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 32px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;line-height: 18.4px;font-family: 微软雅黑, sans-serif\">泰山被古人视为“直通帝座”的天堂，成为百姓崇拜，帝王告祭的神山，有“泰山安，四海皆安”的说法。自秦始皇开始到清代，先后有13代帝王引次亲登泰山封禅或祭祀，另外有24代帝王遣官祭祀72次。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 24px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;line-height: 18.4px;font-family: 微软雅黑, sans-serif\">古代文人雅士更对泰山仰慕备至，纷纷前来游历，作诗记文。泰山宏大的山体上留下了20余处古建筑群，2200余处碑碣石刻。道教、佛教视泰山为“仙山佛国”，神化泰山，在泰山建造了大量宫观寺庙。经石峪的《金刚经》石刻，闻名中外。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;text-indent: 32px;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;line-height: 18.4px;font-family: 微软雅黑, sans-serif\">泰山风景以壮丽著称。重叠的山势，厚重的形体，苍松巨石的烘托，云烟的变化，使它在雄浑中兼有明丽，静穆中透着神奇。自然的泰山，彰显着自然的神奇；文化的泰山，印证着文化的神圣。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;line-height: 18.4px;font-family: 微软雅黑, sans-serif\">泰山是中华民族的象征，是灿烂东方文化的缩影，是“天人合一”思想的寄托之地，是中华民族精神的家园。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><strong><span style=\"font-size: 16px;line-height: 18.4px;font-family: 微软雅黑, sans-serif\">&nbsp; &nbsp; &nbsp;</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong>&nbsp; &nbsp;<span style=\"font-size: 24px\">&nbsp;【岱崮介绍】岱崮是齐鲁名镇，坐落于沂蒙山区腹地，位于山东蒙阴县东北部，毗邻临沂、莱芜、淄博三市交界地带，地质结构十分独特，地貌异彩纷呈，享誉地理学界，这里有“天下第一崮乡”的美誉，是中国大陆首批11个“中国最美小镇”之一，位列第三位。神奇的岱崮地貌是大自然鬼斧神工的神奇杰作！</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong><span style=\"font-size: 24px\">岱崮，是山东名镇，风光奇秀、旖旎，宛如一颗璀璨夺目的明珠，光芒四射，神奇地照耀着这片齐鲁大地。</span></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170527/20170527135302_25751.jpg\" alt=\"\" style=\";padding: 0px;border-width: initial;border-style: none\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170527/20170527135303_42109.jpg\" alt=\"\" style=\";padding: 0px;border-width: initial;border-style: none\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170527/20170527135304_86064.jpg\" alt=\"\" style=\";padding: 0px;border-width: initial;border-style: none\"/></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><img src=\"http://new.chinawalking.net.cn/upload/20160920/14743686341140.jpg\" title=\"泰山.jpg\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><img src=\"http://new.chinawalking.net.cn/upload/20160920/14743686358971.jpg\" title=\"泰山1.jpg\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><img src=\"http://new.chinawalking.net.cn/upload/20160920/14743686355032.jpg\" title=\"泰山2.jpg\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: Tahoma, Verdana;background-color: rgb(243, 243, 243)\"><img src=\"http://new.chinawalking.net.cn/upload/20160920/14743686367171.jpg\" title=\"泰山3.jpg\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p><br/></p>', '8', 3, '2017-11-3', '2017-11-5', 780, '山东泰安', '泰山宾馆', '活动内容你说了算', '2017-11-3 12:12:12', '火车站集合地点', '2', '', '火车', 34, 0, '18328402805', '张翠翠领队', '1504443619.jpg#1504443627.jpg#1504443633.jpg', '', '2017-09-03 13:00:47'),
(10, '9月16—17日【箭扣长城】长城只想去这里~~月光下夜景静谧神秘，月光流动如水，长城起伏似龙', '<p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong>【行程安排】</strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong><br/></strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong>D 1 &nbsp;&nbsp;</strong>&nbsp;&nbsp;</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">早上7点东直门地铁站集合坐916到怀柔北大街，包小面去箭扣村</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">11点到箭扣村简餐12点上长城。西大墙----北京结-----鹰飞倒仰------天梯-----将军守关行程9公里用时6小时</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">19点入住农家院</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong>D 2 &nbsp; &nbsp;&nbsp;</strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">早上4点起床4点半出发将军守关看日出</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">将军守关------箭扣-------歪脖楼------西油楼------正北楼</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">10点西栅栏1队下撤11点上小面回怀柔，12:30怀柔坐小面回北京14:00东直门结束</p><h3 style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;height: 45px;line-height: 45px;color: rgb(75, 142, 232);font-weight: normal;font-size: 18px\">费用说明</h3><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\"><strong>【</strong><strong>费用说明&nbsp;</strong><strong>】</strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">费用包括怀柔包车到箭扣的来回车费，每天一人2瓶水，一晚住宿。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">没有说明的费用除l领队外AA</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px\">老乡可能收进山费和梯子费自费</p><h3 style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;height: 45px;line-height: 45px;color: rgb(75, 142, 232);font-weight: normal;font-size: 18px\">线路描述</h3><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><strong>【线路描述】</strong></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><span style=\"font-family: 宋体;font-size: 12px\">箭扣长城</span><sup></sup><span style=\"font-size: 12px\">&nbsp;</span>&nbsp;<span style=\"font-family: 宋体;font-size: 12px\">位于京郊怀柔县西北八道河乡境内，距怀柔县城约</span><span style=\"font-size: 12px\">30</span><span style=\"font-family: 宋体\"><span style=\"font-size: 12px\">公里，箭扣长城在怀柔县渤海镇（北侧</span><span style=\"font-size: 12px\">箭扣烽火台</span></span><span>&nbsp;</span><span style=\"font-family: 宋体;font-size: 12px\">属八道河，南侧属渤海），往东走就是慕田峪，往西走就是大榛峪。山势非常富于变化，险峰断崖之上的长城也显得更加雄奇险要。箭扣长城因整段长城蜿蜒呈</span><span style=\"font-size: 12px\">W</span><span style=\"font-family: 宋体;font-size: 12px\">状，形如满弓扣箭而得名。箭扣就在慕田峪长城以西</span><span style=\"font-size: 12px\">10</span><span style=\"font-family: 宋体;font-size: 12px\">公里，从慕田峪沿长城走过去，其间景致颇佳，还有著名的</span><span style=\"font-size: 12px\">“</span><span style=\"font-family: 宋体;font-size: 12px\">刀把楼</span><span style=\"font-size: 12px\">”</span><span style=\"font-family: 宋体;font-size: 12px\">（长城的一个小分岔，上建一座形制独特、规模很大的敌楼），以及</span><span style=\"font-size: 12px\">“</span><span style=\"font-family: 宋体;font-size: 12px\">牛犄角边</span><span style=\"font-size: 12px\">”</span><span style=\"font-family: 宋体;font-size: 12px\">。</span><a href=\"http://undefined\" target=\"_blank\" style=\"color: rgb(51, 51, 51);outline: none;transition: all 0.2s ease-in-out\"></a></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><span style=\"font-family: 宋体;font-size: 12px\">登上箭扣，还可以观赏龙潭和古长城砖窑遗址。山脚下平地泉水奔涌泉水汇集成潭，</span><span style=\"font-size: 12px\">“</span><span style=\"font-family: 宋体;font-size: 12px\">潭中鱼可百许头，皆若空游无所依</span><span style=\"font-size: 12px\">”</span><span style=\"font-family: 宋体;font-size: 12px\">，那鱼就是著名的虹鳟鱼。潭下建有目前北方最大的虹鳟鱼养殖场。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><span style=\"font-family: 宋体;font-size: 12px\">箭扣长城是北京一段最险峻、雄奇的长城，自然风化严重，没有任何人工修饰，自牛犄角边、南大楼、鬼门关、东西缩脖楼、东西油篓顶，箭扣梁、将军守关、天梯、鹰飞倒仰、北京接节到九眼楼</span><span style=\"font-size: 12px\">(</span><span style=\"font-family: 宋体;font-size: 12px\">望京楼）绵延</span><span style=\"font-size: 12px\">20</span><span style=\"font-family: 宋体;font-size: 12px\">多公里，充分展现了长城的惊、险、奇、特、绝，能领略到原汁原味的古老的长城景观。天梯、鹰飞倒仰、北京结，九眼楼、一段路最险，从东向西走稍好一点，从西向东走更艰难。遇见倒塌的路段要绕出长城，在城墙的石缝里插上树枝做梯子，再爬墙进去；天梯是一段</span><span style=\"font-size: 12px\">70</span><span style=\"font-family: 宋体;font-size: 12px\">度陡坡、</span><span style=\"font-size: 12px\">70-80</span><span style=\"font-family: 宋体;font-size: 12px\">米长的长城，最窄处</span><span style=\"font-size: 12px\">60</span><span style=\"font-family: 宋体;font-size: 12px\">公分宽，台阶</span><span style=\"font-size: 12px\">40-50</span><span style=\"font-family: 宋体;font-size: 12px\">公分高、</span><span style=\"font-size: 12px\">15</span><span style=\"font-family: 宋体;font-size: 12px\">公分台面，要四肢并用地爬；鹰飞倒仰有一处长城直立倒塌，要南辕北辙地绕道迂回过去，下山</span><span style=\"font-size: 12px\">70-80</span><span style=\"font-family: 宋体;font-size: 12px\">度陡坡，要不抓住树枝，很难站住；北京结到望京楼一段在长城里，东侧是城墙，西侧是灌木，没有台阶，非常难走。</span><sup></sup><span style=\"font-family: 宋体;font-size: 12px\">交通极为便利，距怀柔县</span><span style=\"font-size: 12px\">28</span><span style=\"font-family: 宋体;font-size: 12px\">公里，距离北京</span><span style=\"font-size: 12px\">73</span><span style=\"font-family: 宋体;font-size: 12px\">公里。南有雄伟险峻古长城衬托，北靠京北第一高峰黑坨山（</span><span style=\"font-size: 12px\">1534</span><span style=\"font-family: 宋体;font-size: 12px\">米）。四面环山的</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><span style=\"font-family: 宋体;font-size: 12px\">庄园更显幽静，树木掩映。鸟唱蝉鸣，凭窗眺望，青山绿水，尽收眼底。曲径通幽，恬然安逸，返璞归真，回归自然。远离喧嚣都市的恬静与安闲，令你耳目一新，宛若陶渊明的世外桃源。在这里你可以体味纯朴的民风民情。比方说你可以在园子里亲手采摘果品，农作物等绿色食品，亲身体验农家生活的乐趣。</span><span style=\"font-size: 12px\">“</span><span style=\"font-family: 宋体;font-size: 12px\">采摘东篱下</span>&nbsp;<span style=\"font-family: 宋体;font-size: 12px\">，悠然见长城</span><span style=\"font-size: 12px\">”</span><span style=\"font-family: 宋体;font-size: 12px\">。闲暇之余，您亦可以去登山，在古长城脚下找寻历史的痕迹。是一处寻幽、访古、探险、休闲、健身的绝佳去处。</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809101640_67845.jpg\" alt=\"\" width=\"800\" height=\"600\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809101641_84045.png\" alt=\"\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809101641_87494.png\" alt=\"\" width=\"800\" height=\"930\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100144_26727.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100144_62517.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100145_95348.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100145_32011.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100145_23247.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100300_78310.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100301_93677.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100301_91806.jpg\" alt=\"\" width=\"800\" height=\"1079\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100301_94683.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100302_67959.jpg\" alt=\"\" width=\"800\" height=\"1201\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100406_49672.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100406_53208.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100407_25556.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;font-family: &#39;Microsoft Yahei&#39;, Arial, 宋体, sans-serif;font-size: 14px;white-space: normal\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170809/20170809100407_54757.jpg\" alt=\"\" width=\"800\" height=\"1067\" style=\";padding: 0px;border-width: initial;border-style: none\"/></p><p><br/></p>', '3', 1, '2017-09-16', '2017-09-17', 180, '北京长城', '箭扣长城', '登山,徒步,摄影,背包旅行', '2017-09-16 09：00:00', '东直门地铁站', '2', '', '包车', 34, 0, '18328402805', '小树苗领队', '1504444114.jpg#1504444133.jpg#1504444139.jpg#1504444149.jpg', '', '2017-09-03 13:09:25'),
(11, '每周三奥森公园徒步，夕阳下，葱绿中，徒步走~~', '<div class=\"mdd-item\" id=\"d-hd-1\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">行程安排</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"mdd-zj\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 18px;\">18:00 &nbsp;队员集合(奥森公园南园南门地铁B出口) 队员签到相互介绍。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 18px;\">18:20 &nbsp;简单技术要点讲解及热身准备活动（最好大家一起集结，每周亲切的相聚！！）</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 18px;\">18:30 &nbsp;出发—— 沿5公里塑胶步道逆时针走一圈</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 18px;\">19:30 &nbsp;5公里到达终点后放松整理活动，总结与合影。各回各家。</span></p></div></div></div><div class=\"mdd-item\" id=\"d-hd-2\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">费用说明</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"con\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"font-size: 18px;\">公益活动</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><span style=\"font-size: 18px;\"></span></p></div></div></div><div class=\"mdd-item\" id=\"d-hd-3\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; height: 45px; line-height: 45px; color: rgb(75, 142, 232); font-weight: normal; font-size: 18px;\">线路描述</h3></div><div class=\"mdd-item-con\" style=\"margin: 0px; padding: 15px 0px 0px; line-height: 30px;\"><div class=\"con\" style=\"margin: 0px; padding: 0px;\"><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 18px;\"><span style=\"color: rgb(96, 217, 120);\"><em></em></span></span></p><span style=\"font-size: 16px;\">你见过晚上六点的奥森吗？</span><br/><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px;\"><br/><span style=\"font-size: 16px;\">夕阳缓缓坠入西边的天空，</span><br/><br/><span style=\"font-size: 16px;\">夜晚来临了。</span><br/><br/><br/><span style=\"font-size: 16px;\">我们只要沿着步道、路径，</span><br/><br/><span style=\"font-size: 16px;\">走过沿路两旁的植物生长处，</span><br/><br/><span style=\"font-size: 16px;\">只要静心聆听、细心感受、循光探索，</span><br/><br/><span style=\"font-size: 16px;\">自然能发现丰富的夜间奥森。</span><br/><br/><br/><span style=\"font-size: 16px;\">每周三，我们CVA中国徒步网都会在奥森公园的南门集合</span><br/><br/><span style=\"font-size: 16px;\">欢迎你也来加入我们健康的徒步方式！！！</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><span style=\"font-family: SimSun; font-size: 16px;\"><em>&nbsp;</em></span></p><p class=\"ddzi\" style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: Simsun;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170731/20170731115805_70303.jpg\" alt=\"\" width=\"600\" height=\"450\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/><span style=\"font-size: 16px;\"><em>&nbsp; &nbsp;</em></span></p><p class=\"ddzi\" style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: Simsun;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170728/20170728165851_89249.jpg\" alt=\"\" width=\"600\" height=\"450\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170717/20170717175612_32899.jpg\" alt=\"\" width=\"600\" height=\"800\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p class=\"ddzi\" style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: Simsun;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20170717/20170717175749_64907.jpg\" alt=\"\" width=\"600\" height=\"800\" style=\"margin: 0px; padding: 0px; border-width: initial; border-style: none;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; font-family: Simsun;\"><br/></p></div></div></div><div class=\"mdd-item\" id=\"d-hd-4\" style=\"margin: 0px 0px 35px; padding: 0px; width: 672px; height: auto; overflow: hidden; font-family: &quot;Microsoft Yahei&quot;, Arial, 宋体, sans-serif; font-size: 14px; white-space: normal;\"><div class=\"title\" style=\"margin: 0px; padding: 0px; width: 672px; height: 45px; border-bottom: 1px dashed rgb(75, 142, 232);\"><h3 style=\"margin-top: 0px;