--
-- 表的结构 `works`
--

CREATE TABLE `works` (
  `id` int(11) NOT NULL,
  `uid` int(11) NOT NULL COMMENT '用户id',
  `did` int(11) NOT NULL COMMENT '大赛外键',
  `pics` varchar(150) NOT NULL COMMENT '参赛图片',
  `intro` varchar(200) NOT NULL COMMENT '作品介绍',
  `depiao` int(11) NOT NULL DEFAULT '0' COMMENT '得票',
  `ctime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '参赛时间'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

